    font-size: 16px;
    font-weight: bold;
    cursor: pointer;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 5px;
    border-radius: 5px;
    box-shadow: 0 4px 10px rgba(218, 165, 32, 0.5);
    transition: background-color 0.3s ease, color 0.3s ease;
}

#importFileBtn:hover {
    background-color: gold;
    color: black;
}

#userProfile {
    display: flex;
    align-items: center;
    justify-content: center;
    height: 40px; /* Match the height of the Google sign-in button */
}

#sendEmailsBtn:enabled {
    background-color: black;
    border: 1px solid gold;
    color: gold;
    cursor: pointer;
}

/* Updated progress bar styles to match the second picture */
#progressBarContainer {
    display: none;
    width: 100%;
    background: #ddd;
    border-radius: 25px;
    overflow: hidden;
    margin-top: 10px;
    position: relative;
    height: 30px;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

#progressBar {
    height: 100%;
    background: linear-gradient(to right, gold, black);
    width: 0%;
    border-radius: 25px;
    transition: width 0.5s;
}

#progressBarContainer::after {
    content: "loading...";
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    font-size: 16px;
    color: black;
    font-weight: bold;
}

#cancelButton {
    position: absolute;
    right: 10px;
    top: 2px;
    background: red;
    color: white;
    border: none;
    border-radius: 5px;
    padding: 5px 10px;
    cursor: pointer;
}

/* From Uiverse.io by andrew-demchenk0 */ 
.button {
    position: relative;
    width: 150px;
    height: 40px;
    cursor: pointer;
    display: flex;
    align-items: center;
    border: 1px solid red ;
    background-color: red;
  }

  .button, .button__icon, .button__text {
    transition: all 0.3s;
  }

  .button .button__text {
    transform: translateX(30px);
    color: #fff;
    font-weight: 600;
  }

  .button .button__icon {
    position: absolute;
    transform: translateX(109px);
    height: 100%;
    width: 39px;
    background-color: red;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .button .svg {
    width: 30px;
    stroke: #fff;
  }

  .button:hover {
    background: red;
  }

  .button:hover .button__text {
    color: transparent;
  }

  .button:hover .button__icon {
    width: 148px;
    transform: translateX(0);
  }

  .button:active .button__icon {
    background-color: black;
  }

  .button:active {
    border: 1px solid black;
  }

/* Email History Styles */
.history-btn {
    background: linear-gradient(45deg, #9b59b6, #8e44ad);
}

.history-btn:hover {
    background: linear-gradient(45deg, #8e44ad, #7d3c98);
}

.history-table-container {
    border: 1px solid #ddd;
    border-radius: 4px;
    overflow: hidden;
}

#historyTable {
    font-size: 14px;
}

#historyTable th {
    background-color: #f8f9fa !important;
    font-weight: bold;
    position: sticky;
    top: 0;
    z-index: 10;
}

#historyTable tr:nth-child(even) {
    background-color: #f9f9f9;
}

#historyTable tr:hover {
    background-color: #e3f2fd;
}

.delete-history-btn:hover {
    background: #cc3333 !important;
}

/* Already sent email indicator */
.row-checkbox:disabled {
    opacity: 0.5;
    cursor: not-allowed;
}
