const { app, BrowserWindow, ipcMain, dialog, shell } = require("electron");
const path = require("path");
const { searchGoogleMaps, cancelScraping } = require("./googleMaps");
const fs = require("fs");
const xlsx = require("xlsx");
const { google } = require("googleapis");
const http = require("http");
const url = require("url");
const EmailDatabase = require("./database");

let mainWindow;
let oauth2Client = null;
let authServer = null; // Track the OAuth server
let scrapingProcess = null;
let emailSendingProcess = null;
let userProfile = null;
let emailDB = null;

// Email normalization function to ensure consistent email cleaning across the app
function normalizeEmail(email) {
  if (!email || typeof email !== 'string') {
    return '';
  }

  return email
    .replace(/\s*\(estimated\)\s*/gi, '') // Remove "(estimated)" case-insensitive
    .replace(/\s*\(.*?\)\s*/g, '') // Remove any other text in parentheses
    .trim()
    .toLowerCase(); // Convert to lowercase for consistent comparison
}

app.whenReady().then(() => {
  emailDB = new EmailDatabase();

  mainWindow = new BrowserWindow({
    width: 1200,
    height: 800,
    icon: path.join(__dirname, "/src/img/bcdq.png"),
    webPreferences: {
      preload: path.join(__dirname, "preload.js"),
      nodeIntegration: false,
      contextIsolation: true,
    },
  });

  mainWindow.loadFile("index.html");
  mainWindow.maximize();
  mainWindow.setMenuBarVisibility(false);
});

ipcMain.on("run-scraper", async (event, query) => {
  try {
    // Prevent starting new scraping if one is already running
    if (scrapingProcess) {
      event.reply("scraper-status", "âš ï¸ Scraping already in progress. Please cancel current process first.");
      return;
    }

    scrapingProcess = searchGoogleMaps(query, event);
    await scrapingProcess;

    // Reset process tracking when completed
    scrapingProcess = null;
  } catch (error) {
    // Reset process tracking on error
    scrapingProcess = null;
    event.reply("scraper-status", `âŒ Error: ${error.message}`);
  }
});

ipcMain.on("cancel-scraper", async (event) => {
  try {
    if (scrapingProcess) {
      console.log("Cancelling scraping process...");

      // Wait for the cancellation to complete properly
      try {
        await cancelScraping();
        // Wait for the process to actually finish cancelling
        await scrapingProcess.catch(() => {}); // Catch any errors from the cancelled process
      } catch (cancelError) {
        console.error("Error during cancellation:", cancelError);
      }

      scrapingProcess = null;
      event.reply("scraper-status", "âŒ Scraping cancelled by user");
      console.log("Scraping process successfully cancelled and reset");
    } else {
      event.reply("scraper-status", "â„¹ï¸ No scraping process to cancel");
    }
  } catch (error) {
    console.error("Error cancelling scraper:", error);
    // Ensure process is reset even if cancellation fails
    scrapingProcess = null;
    event.reply("scraper-status", "âŒ Error cancelling scraper, process reset");
  }
});

oauth2Client = new google.auth.OAuth2(
  (process.env.GOOGLE_CLIENT_ID =
    "82150396052-rnvsou8p8gp54cf235gc59eagtcgie9n.apps.googleusercontent.com"),
  (process.env.GOOGLE_CLIENT_SECRET = "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"),
  "http://localhost:3000/oauth2callback"
);

ipcMain.on("google-auth", async (event) => {
  try {
    if (!process.env.GOOGLE_CLIENT_ID || !process.env.GOOGLE_CLIENT_SECRET) {
      throw new Error("Google credentials not configured");
    }

    const authUrl = oauth2Client.generateAuthUrl({
      access_type: "offline",
      scope: [
        "https://www.googleapis.com/auth/gmail.send",
        "https://www.googleapis.com/auth/userinfo.profile",
        "https://www.googleapis.com/auth/userinfo.email",
      ],
      prompt: "consent select_account",
    });

    // Close previous server if still running
    if (authServer) {
      try {
        authServer.close();
      } catch (e) {}
      authServer = null;
    }

    authServer = http.createServer(async (req, res) => {
      const { code } = url.parse(req.url, true).query;

      if (code) {
        try {
          const { tokens } = await oauth2Client.getToken(code);
          oauth2Client.setCredentials(tokens);

          const oauth2 = google.oauth2({ version: "v2", auth: oauth2Client });
